def test_distance_naive(G,u,v):
    assert requetes.distance_naive(G,"Bill Durks","Greg Lewis") == 4
    assert requetes.distance_naive(G,"Bill Durks","[[Marcia Jean Kurtz]]") == 2
    assert requetes.distance_naive(G,"","") == 0
